import { FontAwesome } from '@expo/vector-icons';
import Button from '../Button';
import { useState } from 'react';

export default function UserPage() {
  const [name, setName] = useState()

  return (
    <View style={styles.container}>
        <View className="personInfo" style={styles.personInfo}>
            <FontAwesome name="user-circle" size={90} color="white" />
            <Text className="name" style={styles.anun}>Anun Azganun</Text>
        </View>
        <View className="changeName" style={styles.changeName}>
            <TextInput placeholder={"Change Your name"}
                       placeholderTextColor={"#ABABAC"} 
                       style={styles.changeNameInput}
                       value={name}
                       onChange={(e)}
                       className="changeNameInput"/>
            <Button>Change name</Button>
        </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    textColor: '#00FF00',
    alignItems: "center",
    justifyContent: 'flex-start',
    flexDirection: 'column',
  },
  personInfo: {
    marginTop: 200,
    flex: 1,
    alignItems: "center",
  },
  anun: {
      color: '#fff',
      fontSize: 25,
      marginTop: 10,
  },
  changeName: {
      flex: 1,
      marginBottom: 200,
      alignItems: 'center',
  },
  changeNameInput: {
      width: 300,
      height: 50,
      fontSize: 16,
      borderRadius: 3,
      backgroundColor: '#1E2021',
      borderRadius: 10,
      paddingVertical: 10,
      paddingHorizontal: 17,
      marginBottom: 30,
  }
});
