const HomePage = ({ navigation }) => {
    return (
        <View style={styles.container}>
            <View style={styles.hello}>
                <Image
                    style={styles.logo}
                    source={{
                        uri: 'https://cdn-icons-png.flaticon.com/512/149/149071.png',
