import { TextInput, View, StyleSheet, TouchableHighlight } from 'react-native'
import React from 'react'
import Button from '../Button'
import { useState } from 'react/cjs/react.production.min'

const LoginPage = ({ navigation }) => {
  const [name, setName] = useState('')
  const [value, setValue] = useState('')

  const Press = () => {
    navigation.navigate('User')
    setName()
  }

  return (
    <View style={styles.container}>
      <TextInput placeholderTextColor="#ABABAC" value={value} onChange={(e) => setValue(e.target.value)} placeholder='Login' style={styles.input} />
      <TextInput placeholderTextColor="#ABABAC" placeholder='Password' style={styles.input} />
      <Button style={styles.btn} onPress={Press}>Login</Button>
      <View style={styles.text}>
       <Button type='easy' onPress={navigation.navigate('Register')}>Registration</Button>
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#120E0B',
    alignItems: 'center',
    justifyContent: 'center',
    width: '80%',
  },
  input: {
    width: '80vw',
    backgroundColor: '#1E2021',
    borderRadius: 10,
    paddingVertical: 15,
    paddingHorizontal: 17,
    marginBottom: 15,
    color: '#fff'
  },
  btn: {
    marginBottom: 15,
  },
  text: {
    width: '80vw',
    marginTop: '-15px',
    alignItems: 'flex-end',
    flexDirection: 'column',
    marginTop: 5,
  }
});

export default LoginPage;
